//create a delete user component
//http://localhost:8080api/v1//delete-user/{id}

import React, { Component } from 'react';
import { Link, Outlet } from 'react-router-dom';
import axios from 'axios';

export default function DeleteUser() {
    const deleteUser = () => {
        axios.delete('http://localhost:8080/api/v1/delete-user/1')
        .then(res => {
            console.log(res);
            console.log(res.data);
        })
    };

    //only render delete user button if user is logged in
    let  userid = localStorage.getItem("token") == null ? false : true;

    return (
        <div>
            {userid ? <button onClick={deleteUser}>Delete User</button> : <Link to="/login">Login</Link>}
        </div>
    );
}
