import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Recipe = () => {
const [recipe, setRecipe] = useState({});
const [ingredients, setIngredients] = useState([]);
//the search should come from Recipes.js
const [search, setSearch] = useState('');


useEffect(() => {
    const recipe = window.location.state.recipe;
    setRecipe(recipe);
    setIngredients(recipe.ingredients);
}
, []);

//check for token
const token = localStorage.getItem('token');
if(!token) {
    window.location.href = '/login';
}

//getRecipe from http://localhost:8080/api/v1/recipes/getRecipe

const getRecipe = () => {
    axios.get(`http://localhost:8080/api/v1/recipes/getRecipe/${search}`, {
        headers: {
            'Authorization': `Bearer ${token}`
        }
    })
    .then(res => {
        setRecipe(res.data);
        setIngredients(res.data.ingredients);
    })
    .catch(err => console.log(err));
}

const searchIngredient = (e) => {
    e.preventDefault();
    getRecipe();
}





return (
    <div>
        <h1>{recipe.recipeName}</h1>
        <h3>Ingredients</h3>
        <ul>
            {ingredients.map((ingredient, index) => (
                <li key={index}>{ingredient}</li>
            ))}
        </ul>
        <form onSubmit={searchIngredient}>
            <input type="text" placeholder="Search by ingredient" onChange={e => setSearch(e.target.value)} />
            <button type="submit">Search</button>
        </form>
    </div>
)
}

export default Recipe;
