import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Recipe = () => {
const [recipe, setRecipe] = useState({});
const [ingredients, setIngredients] = useState([]);
const [search, setSearch] = useState('');

useEffect(() => {
const token = localStorage.getItem('token');
if (token) {
    axios.get(`http://localhost:8080/api/v1/recipes/getRecipe?name=${search}`, {

headers: {
'Authorization': `Bearer ${token}`
}
})

.then(res => {
setRecipe(res.data);
setIngredients(res.data.ingredients);
})
.catch(err => console.log(err));
} else {
window.location.href = '/login';
}
}, [search]);

const handleSubmit = (e) => {
e.preventDefault();
setSearch(e.target.value);
}

return (
<div>
<form onSubmit={handleSubmit}>
<label htmlFor="search">Search for a recipe:</label>
<input
type="text"
id="search"
value={search}
onChange={(e) => setSearch(e.target.value)}
/>
<button onClick={() => {
                //axios get request here
            }}>Search</button>
</form>
<h1>{recipe.recipeName}</h1>
<p>Prep Time: {recipe.prepTime}</p>
<p>Cook Time: {recipe.cookTime}</p>
<p>Servings: {recipe.servings}</p>
<p>Instructions: {recipe.instructions}</p>
<h2>Ingredients</h2>
<ul>
{ingredients.map(ingredient => (
<li key={ingredient.ingredientId}>{ingredient.ingredientName} - {ingredient.quantity}</li>
))}
</ul>
</div>
);

}

export default Recipe;
