import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function UpdateUser() {

const [id, setId] = useState("");
const [username, setUsername] = useState("");
const [email, setEmail] = useState("");
const [role, setRole] = useState("");
const [message, setMessage] = useState("");
const [messageColor, setMessageColor] = useState("");
const [users, setUsers] = useState([]);

const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    try {
        const res = await axios.patch(`http://localhost:8080/api/v1/users/update-user/${id}`, {
            username,
            email,
            role
        }, {
            headers: {
                Authorization: `Bearer ${token}`,
            },
        });
        setMessage(res.data);
        setMessageColor("text-green-500");
    } catch (err) {
        if (err.response.status === 403) {
            setMessage("You are not authorized to update this user.");
            setMessageColor("text-red-500");
        } else {
            setMessage("An error occurred. Please try again later.");
            setMessageColor("text-red-500");
            console.log(err);
        }
    }
};

useEffect(() => {
    const fetchUsers = async () => {
        const token = localStorage.getItem("token");
        try {
            const res = await axios.get("http://localhost:8080/api/v1/users/get-all-users", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setUsers(res.data);
        } catch (err) {
            if (err.response.status === 403) {
                setMessage("You are not authorized to view the users.");
                setMessageColor("text-red-500");
            } else {
                setMessage("An error occurred. Please try again later.");
                setMessageColor("text-red-500");
                console.log(err);
            }
        }
    };
    fetchUsers();
}, []);


return (
    <div className="bg-blue-500 flex flex-col items-center justify-center min-h-screen py-2">
        <div className="bg-white p-6 rounded-lg w-full flex-1">
            <h1 className="text-2xl font-bold text-center">Update User</h1>
            <form className="pt-4" onSubmit={handleSubmit}>
                <div className="mb-4">
                    <input
                        className="border border-gray-400 p-2 rounded-lg w-full"
                        type="text"
                        placeholder="Enter User ID"
                        value={id}
                        onChange={(e) => setId(e.target.value)}
                    />
                </div>
                <div className="mb-4">
                    <input
                        className="border border-gray-400 p-2 rounded-lg w-full"
                        type="text"
                        placeholder="Enter Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>
                <div className="mb-4">
                    <input
                        className="border border-gray-400 p-2 rounded-lg w-full"
                        type="email"
                        placeholder="Enter Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>
                <div className="mb-4">
                    <input
                        className="border border-gray-400 p-2 rounded-lg w-full"
                        type="text"
                        placeholder="Enter Role"
                        value={role}
                        onChange={(e) => setRole(e.target.value)}
                    />
                </div>
                <div className="mb-4">
                    <button
                        className="bg-blue-500 text-white p-2 rounded-lg w-full"
                        type="submit"
                    >
                        Update User
                    </button>
                </div>
                <div className="mb-4">
                    <Link
                        className="bg-blue-500 text-white p-2 rounded-lg w-full"
                        to="/users"
                    >
                        View Users
                    </Link>
                </div>
                <div className="mb-4">
                    <Link
                        className="bg-blue-500 text-white p-2 rounded-lg w-full"
                        to="/"
                    >
                        Home
                    </Link>
                </div>
                <div className="mb-4">
                    <p className={messageColor}>{message}</p>
                </div>
            </form>
        </div>
    </div>
);
}

export default UpdateUser;
