//delete method on http://localhost:8080/api/v1/delete-user/{id}

import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default function DeleteUser() {
    const [id, setId] = useState("");
    const [message, setMessage] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        const token = localStorage.getItem("token");
        axios
        .delete(`http://localhost:8080/api/v1/delete-user/${id}`, {
            headers: {
            Authorization: `Bearer ${token}`,
            },
        })
        .then((res) => {
            setMessage(res.data);
            setId("");
        })
        .catch((err) => console.log(err));
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen py-2">
        <div className="flex flex-col items-center justify-center w-full flex-1 px-20 text-center">
            <br />
            <br />
            <h1 className="text-6xl font-bold">Delete User</h1>
            <br />
            <div className="flex flex-wrap items-center justify-around max-w-4xl mt-6 sm:w-full">
            <div className="grid grid-cols-2 gap-4">
                <form onSubmit={handleSubmit}>
                <label htmlFor="id">User ID</label>
                <input
                    type="text"
                    id="id"
                    value={id}
                    onChange={(e) => setId(e.target.value)}
                />
                <br />
                <br />
                <button type="submit">Delete User</button>
                </form>
                <br />
                <br />
                <div className="flex flex-col items-center justify-center w-full flex-1 px-20 text-center">
                <br />
                <br />
                <h1 className="text-6xl font-bold">{message}</h1>
                <br />
                <div className="flex flex-wrap items-center justify-around max-w-4xl mt-6 sm:w-full">
                    <div className="grid grid-cols-2 gap-4">
                    <Link to="/dashboard" className="back_btn">
                        Back
                    </Link>
                    </div>
                </div>
                </div>
            </div>
            </div>
        </div>
        </div>
    );
    }
