import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Recipes() {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if(!token) {
            window.location.href = '/login';
        } else {
            axios.get('http://localhost:8080/api/v1/recipes/getAllRecipes', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            })
            .then(res => setRecipes(res.data))
            .catch(err => console.log(err));
        }
    }, []);

    return (
        //Link to selected recipe and send that recipe as state to the Recipe.js component
        <div>
            <h1>All Recipes</h1>
            <ul>
                {recipes.map((recipe, index) => (
                    <li key={index}>
                        <Link to={{pathname: '/recipe', state: {recipe: recipe}}}>{recipe.recipeName}</Link>
                    </li>
                ))}
            </ul>
        </div>

    );
}

export default Recipes;
