//create a private route based on jwt token
//if login attempt generates a token, navigate to protected route of Dashboard
//if login attempt fails, navigate to Login page
//if user is not logged in, navigate to Login page
//if user is logged in, navigate to Dashboard

import React from 'react'
import { Navigate, Router, Routes, Route } from 'react-router-dom'
import Dashboard from './Dashboard';


export default function PrivateRoutes() {

    //private routes for logged in users

    let  userid = localStorage.getItem("token") == null ? false : true;

    return (
        <div>
            <Router>
                <Routes>
                    <Route path="/dashboard" element={userid ? <Dashboard /> : <Navigate to="/login" />} />
                    <Route path="/deleteuser" element={userid ? <DeleteUser /> : <Navigate to="/login" />} />
                </Routes>
            </Router>

        </div>
    )
}
