import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
/*import styles from "./Recipes.module.css";*/

function Recipes() {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      window.location.href = "/login";
    } else {
      axios
        .get("http://localhost:8080/api/v1/recipes/getAllRecipes", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((res) => setRecipes(res.data))
        .catch((err) => console.log(err));
    }
  }, []);

  //store the recipeName and send it to the recipe page
  const handleClick = (recipeName) => {
    localStorage.setItem("recipeName", recipeName);
  };

  /*return (
    <div className={styles.container}>
    {recipes.map((recipe) => (
    <div className={styles.recipeBox} key={recipe.recipeId}>
    <h2>{recipe.recipeName}</h2>
    <p>Prep Time: {recipe.prepTime} minutes</p>
    <p>Cook Time: {recipe.cookTime} minutes</p>
    <p>Servings: {recipe.servings}</p>
    <Link to="/recipe" onClick={() => handleClick(recipe.recipeName)}>View Recipe</Link>
    </div>
    ))}
    <a href="/dashboard" className={styles.back_btn}>Back</a>
    </div>
    );
    }

export default Recipes;*/

//return using tailwind
return (
  <div className="flex flex-col items-center justify-center min-h-screen py-2">
    <div className="flex flex-col items-center justify-center w-full flex-1 px-20 text-center">
      <h1 className="text-6xl font-bold">Recipes</h1>
      <div className="flex flex-wrap items-center justify-around max-w-4xl mt-6 sm:w-full">
        {recipes.map((recipe) => (
          <div
            className="flex flex-col items-center justify-center max-w-xs p-6 mt-6 bg-white rounded-xl shadow-md sm:flex-1 sm:p-8"
            key={recipe.recipeId}
          >
            <div className="flex items-center justify-center w-12 h-12 mb-4 rounded-full bg-indigo-100">
              <svg
                className="w-8 h-8 text-indigo-500"
                fill="none"
                stroke-linecap="round"
                stroke-linejoin="round"
                stroke-width="2"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path d="M12 14l9-5-9-5-9 5 9 5z"></path>
                <path d="M12 14l-9 5 9 5 9-5-9-5z"></path>
                <path d="M12 14L12 3"></path>
              </svg>
            </div>
            <div className="mt-4">
              <h2 className="text-xl font-medium text-gray-900 title-font">
                {recipe.recipeName}
              </h2>
              <p className="mt-2 text-base text-gray-600">
                Prep Time: {recipe.prepTime} minutes
              </p>
              <p className="mt-2 text-base text-gray-600">
                Cook Time: {recipe.cookTime} minutes
              </p>
              <p className="mt-2 text-base text-gray-600">
                Servings: {recipe.servings}
              </p>
              <Link
                to="/recipe"
                onClick={() => handleClick(recipe.recipeName)}
                className="inline-flex items-center px-4 py-2 mt-4 text-base font-medium text-white bg-indigo-500 border-0 rounded-md focus:outline-none hover:bg-indigo-600"
              >
                View Recipe
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  </div>
);
}

export default Recipes;
