import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import styles from "./Recipes.module.css";

function Recipes() {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      window.location.href = "/login";
    } else {
      axios
        .get("http://localhost:8080/api/v1/recipes/getAllRecipes", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((res) => setRecipes(res.data))
        .catch((err) => console.log(err));
    }
  }, []);

  //store the recipeName and send it to the recipe page
  const handleClick = (recipeName) => {
    localStorage.setItem("recipeName", recipeName);
  };

  return (
    <div className={styles.container}>
    <h1 className={styles.header}>Recipes</h1>
    <div className={styles.gridContainer}>
    {recipes.map((recipe) => (
    <div key={recipe.recipeId} className={styles.gridItem}>
    <Link to="/recipe" onClick={() => handleClick(recipe.recipeName)}>
    <p className={styles.recipeName}>{recipe.recipeName}</p>
    </Link>
    </div>
    ))}
    </div>
    <button className={styles.backBtn} onClick={() => (window.location.href = "/dashboard")}>
    Back
    </button>
    </div>
    );
    }

export default Recipes;
