import React, { useState, useEffect } from 'react'
import axios from 'axios'

const Recipes = () => {

const [recipeName, setRecipeName] = useState('')
const [recipes, setRecipes] = useState([])

//check for token in local storage
const isSignedIn = localStorage.getItem('token')

useEffect(() => {
    const getRecipes = async () => {
        setIsLoading(true)
        try {
            const token = localStorage.getItem('token')
            const headers = {
                Authorization: `Bearer ${token}`
            }
            const res = await axios.get(`http://localhost:8080/api/v1/recipes?recipeName=${recipeName}`, { headers })
            setRecipes(res.data)
            setIsLoading(false)
        } catch (err) {
            setError(err)
            setIsLoading(false)
        }
    }
    getRecipes()
}, [recipeName])

return (
    <div>
        <form onSubmit={handleSubmit}>
            <input type="text" value={recipeName} onChange={(e) => setRecipeName(e.target.value)} placeholder="Search for a recipe" />
            <button type="submit">Search</button>
        </form>
        <div>
            {recipes.map(recipe => (
                <div key={recipe.recipeId}>
                    <h3>{recipe.recipeName}</h3>
                    <p>Prep Time: {recipe.prepTime}</p>
                    <p>Cook Time: {recipe.cookTime}</p>
                    <p>Servings: {recipe.servings}</p>
                    <h4>Instructions:</h4>
                    <p>{recipe.instructions}</p>
                    <h4>Ingredients:</h4>
                    <ul>
                        {recipe.ingredients.map(ingredient => (
                            <li key={ingredient.ingredientId}>{ingredient.ingredientName}</li>
                        ))}
                    </ul>
                </div>
            ))}
        </div>
    </div>
)
}


export default Recipes
