import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Recipe = () => {
const [recipe, setRecipe] = useState({});
const [ingredients, setIngredients] = useState([]);
//i want the search to come from the recipes page


//get {recipe.recipeName} from Recipes.js
const search = window.location.search;


//get the recipeName from the search
const params = new URLSearchParams(search);

const recipeName = params.get('recipeName');

//get the recipe from the database
useEffect(() => {
const token = localStorage.getItem('token');
if (!token) {
window.location.href = '/login';
} else {
axios
.get(`http://localhost:8080/api/v1/recipes/getRecipe/${recipeName}`, {
headers: {
Authorization: `Bearer ${token}`,
},
})
.then((res) => {
setRecipe(res.data);
setIngredients(res.data.ingredients);
})
.catch((err) => console.log(err));
}
}, []);

return (
<div>
    <h1>Recipe</h1>
    <h2>{recipe.recipeName}</h2>
    <h3>Ingredients</h3>
    <ul>
        {ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
        ))}
    </ul>
</div>
);
};

export default Recipe;
