import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import styles from "./Recipes.module.css";

function Recipes() {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      window.location.href = "/login";
    } else {
      axios
        .get("http://localhost:8080/api/v1/recipes/getAllRecipes", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((res) => setRecipes(res.data))
        .catch((err) => console.log(err));
    }
  }, []);

  //store the recipeName and send it to the recipe page
  const handleClick = (recipeName) => {
    localStorage.setItem("recipeName", recipeName);
  };

  return (
    <div className={styles.container}>
        {recipes.map((recipe) => (
            <div className={styles.box} key={recipe.recipeId}>
                <h2>{recipe.recipeName}</h2>
                <p>Prep Time: {recipe.prepTime}</p>
                <p>Cook Time: {recipe.cookTime}</p>
                <p>Servings: {recipe.servings}</p>
                <Link to="/recipe" onClick={() => handleClick(recipe.recipeName)}>
                    View Recipe
                </Link>
            </div>
        ))}
        <button className={styles.back-btn} onClick={() => (window.location.href = "/dashboard")}>
            Back
        </button>
    </div>
);
}

export default Recipes;
