import React, { useState, useEffect, Navigate} from "react";

export default function Dashboard() {
const [isExpired, setIsExpired] = useState(false);

useEffect(() => {
setInterval(() => {
var expiration = localStorage.getItem("expiration");
if (expiration && Date.now() > expiration) {
localStorage.removeItem("token");
localStorage.removeItem("expiration");
alert("Your session has expired. Please login again.");
setIsExpired(true);
}
}, 1000);
}, []);

if (isExpired) {
return <Navigate to="/login" />;
}

const logout = () => {
localStorage.removeItem("token");
localStorage.removeItem("expiration");
window.location.href = "/";
};

return (
<div className="flex flex-col items-center justify-center min-h-screen py-2 bg-gray-100 sm:px-6 lg:px-8">
<div className="flex flex-col items-center justify-center w-full flex-1 px-20 text-center">
<h1 className="text-8xl font-bold">Welcome to your very own recipes application!</h1>
<div className="flex flex-wrap items-center justify-around max-w-4xl mt-6 sm:w-full">
<div className="w-full p-6 mt-6 text-left border rounded-xl sm:w-1/2">
<button
className="w-full px-4 py-2 font-bold text-white bg-blue-500 rounded hover:bg-blue-700"
onClick={() => window.location.href = "/recipes"}
>
Recipes
</button>
<br />
<br />
<button
           className="w-full px-4 py-2 font-bold text-white bg-blue-500 rounded hover:bg-blue-700"
           onClick={logout}
         >
Logout
</button>
</div>
</div>
</div>
</div>
);
}

//Whenever Failed to load resource: the server responded with a status of 403 () error occurs, redirect to login page and say that the session has expired. Add this code to handle the 403 error:

window.addEventListener('error', function (event) {
if (event.target.status === 403) {
localStorage.removeItem("token");
localStorage.removeItem("expiration");
alert("Your session has expired. Please login again.");
setIsExpired(true);
}
});
