import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Recipes() {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if(!token) {
            window.location.href = '/login';
        } else {
            axios.get('http://localhost:8080/api/v1/recipes/getAllRecipes', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            })
            .then(res => setRecipes(res.data))
            .catch(err => console.log(err));
        }
    }, []);    

    //store the recipeName and send it to the recipe page
    const handleClick = (recipeName) => {
        localStorage.setItem('recipeName', recipeName);
    }





    return (
        <div>
            <h1>Recipes</h1>
            <ul>
                {recipes.map(recipe => (
                    <li key={recipe.recipeId}>
                        <Link to="/recipe" onClick={() => handleClick(recipe.recipeName)}>{recipe.recipeName}</Link>
                    </li>
                ))}
            </ul>

        </div>
    );
}

export default Recipes;
